package class6.copy;

public class Displaynumberswhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i =0;
		int count = 0;
		while(i<=100){
			System.out.println("the value is " +i);
			count++;
			i++;
		}
		System.out.println(" \n No of digits are " +count);
	}
}
